不多说,上代码：
// app.jsximport React, &#123; useState &#125; from &quot;react&quot;;export default function App() &#123;//定义主组件  const [advice, setAdvice] = useState(&quot;&quot;);  const [count, setCount] = useState(&quot;&quot;);  async function getAdvice(params) &#123;    const res = await fetch(&quot;url&quot;);    const data = await res.json();    setAdvice(data.slip.advice);    setCount((c) =&gt; c + 1);  &#125;  useEffect(() =&gt; &#123;    //定义useEffect进行初始化,useEffect 钩子在组件挂载时调用getAdvice函数,以获取初始的建议数据。空数组[]作为第二个参数,确保该副作用只在组件挂载时执行一次。    getAdvice();  &#125;, []);  return (    &lt;div&gt;      &lt;h1&gt;&#123;advice&#125;&lt;/h1&gt;      &lt;button onclick=&#123;getAdvice&#125;&gt;Get advice&lt;/button&gt;      &lt;Message count=&#123;count&#125;&gt;&lt;/Message&gt;    &lt;/div&gt;  );&#125;function Message(props) &#123;//简单的函数组件  return (    &lt;p&gt;      you have read &lt;strong&gt;&#123;props.count&#125;&lt;/strong&gt; pieces of advice    &lt;/p&gt;  );&#125;
